import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './contact.css';


class Contact extends Component {
    // static propTypes    =   {
    //     name:   PropTypes.string.isRequired,
    //     email:  PropTypes.string.isRequired,
    //     phone:  PropTypes.string.isRequired
    // }
    //construction
    // constructor(){
    //     super();
    //     this.state  =   {};
    //     this.onShowClick    =   this.onShowClick.bind(this);
    // }
    state = {
        showContactInfo :   false
    };

    // //onShowClick
    // onShowClick = (e) => {
    //     this.setState({showContactInfo:!this.state.showContactInfo })
    // };

    //onDeleteClick
    onDeleteClick = () => {
        this.props.deleteClickHandler();
    }

    render() {
        const {id,name,email,phone}    =   this.props.contact;
        const { showContactInfo }   =   this.state;
        return (
            <React.Fragment>
                <div className="container">
                    <div className="my-3 p-3 bg-white rounded box-shadow">
                        <h6 className=" pb-2 mb-0">{name} 
                            <i onClick={() => this.setState({ showContactInfo: !this.state.showContactInfo })} className="fa fa-caret-down" style={{ cursor:'pointer' }} ></i> 
                            <i className="fa fa-times" style={{ cursor:'pointer',float:'right',color:'red' }} onClick={ this.onDeleteClick(id) } ></i>
                        </h6>

                        {showContactInfo ? (
                            <div className="media text-muted pt-3 border-gray">
                                <div className="media-body pb-3 mb-0 small lh-125 border-bottom ">
                                    <span className="d-block ">{email}</span>
                                    <span className="d-block">{phone}</span>
                                </div>
                            </div>
                        ) : null }


                    </div>
                </div>
            </React.Fragment>
        )
    }
}

Contact.propTypes   =   {
    contact:   PropTypes.object.isRequired,
    deleteClickHandler:PropTypes.func.isRequired
}

export default Contact;
