import React, { Component } from 'react';
import Contact from './Contact';

import { Consumer } from '../context';

class Contacts extends Component {

    deleteContact = id =>{ 
        const { contacts } = this.state;


        // const newContacts = this.state.filter(contact=>contact.id !== id );

        // this.setState({
        //     contacts:newContacts
        // });
    }

    render() {
        return (
            <Consumer>
                {value => {
                    const { contacts } = value;
                    return (
                        <React.Fragment>
                            {contacts.map((contact, index) =>
                                <Contact key={contact.id} deleteClickhandler={this.deleteContact.bind(this,contact.id)} contact={contact}  ></Contact>
                            )}
                        </React.Fragment>
                    )
                }}
            </Consumer>
        )

    }
}
export default Contacts;
